mapper.select:
SELECT COUNT(*) FROM sql_metadata AS A WHERE A.name LIKE #{cond.name,jdbcType=VARCHAR} AND A.registered_at >= #{cond.registeredFrom,jdbcType=TIMESTAMP} AND A.registered_at < #{cond.registeredTo,jdbcType=TIMESTAMP} AND (
(A.published_flg = 0 AND A.owned_by = #{cond.loginId,jdbcType=VARCHAR}) OR A.published_flg <> 0
A.published_flg <> 0 OR A.owned_by = #{cond.loginId,jdbcType=VARCHAR}
) AND
A.sql_type IN (
'select', 'any', 'csv'
) AND
A.deleted_flg = 0

SELECT A.id, A.sql_type, A.name, A.description, A.owned_by, A.published_flg, A.registered_at, A.updated_at, A.created_at, A.deleted_flg FROM sql_metadata AS A WHERE A.name LIKE #{cond.name,jdbcType=VARCHAR} AND A.registered_at >= #{cond.registeredFrom,jdbcType=TIMESTAMP} AND A.registered_at < #{cond.registeredTo,jdbcType=TIMESTAMP} AND (
(A.published_flg = 0 AND A.owned_by = #{cond.loginId,jdbcType=VARCHAR}) OR A.published_flg <> 0
A.published_flg <> 0 OR A.owned_by = #{cond.loginId,jdbcType=VARCHAR}
) AND
A.sql_type IN (
'select', 'any', 'csv'
) AND
A.deleted_flg = 0 LIMIT #{limit,jdbcType=INTEGER} OFFSET #{offset,jdbcType=INTEGER}
